"use strict";(self.webpackChunkcivicsaurus=self.webpackChunkcivicsaurus||[]).push([[910],{7945:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Welcome","href":"/civicsaurus/docs/intro","docId":"intro","unlisted":false},{"type":"category","label":"1. Principles","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"1.1 User Centered and Inclusive","href":"/civicsaurus/docs/principles/user-centered-and-inclusive","docId":"principles/user-centered-and-inclusive","unlisted":false},{"type":"link","label":"1.2 Open and Transparent","href":"/civicsaurus/docs/principles/open-and-transparent","docId":"principles/open-and-transparent","unlisted":false},{"type":"link","label":"1.3 Iterative and Responsive","href":"/civicsaurus/docs/principles/iterative-and-responsive","docId":"principles/iterative-and-responsive","unlisted":false},{"type":"link","label":"1.4 Sustainable and Scalable","href":"/civicsaurus/docs/principles/sustainable-and-scalable","docId":"principles/sustainable-and-scalable","unlisted":false}],"href":"/civicsaurus/docs/category/1-principles"},{"type":"category","label":"2. Ways of Working","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"2.1 Vision Setting","href":"/civicsaurus/docs/ways-of-working/orientation-and-vision-setting","docId":"ways-of-working/orientation-and-vision-setting","unlisted":false},{"type":"link","label":"2.2 Connection and Relationship","href":"/civicsaurus/docs/ways-of-working/connection-and-relationship","docId":"ways-of-working/connection-and-relationship","unlisted":false},{"type":"link","label":"2.3 Leadership and Storytelling","href":"/civicsaurus/docs/ways-of-working/leadership-and-storytelling","docId":"ways-of-working/leadership-and-storytelling","unlisted":false},{"type":"link","label":"2.4 Continuing the Journey","href":"/civicsaurus/docs/ways-of-working/continuing-the-journey","docId":"ways-of-working/continuing-the-journey","unlisted":false}],"href":"/civicsaurus/docs/category/2-ways-of-working"},{"type":"category","label":"3. Project Lifecycle","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"3.1 Discover","href":"/civicsaurus/docs/project-lifecycle/explore","docId":"project-lifecycle/explore","unlisted":false},{"type":"link","label":"3.2 Define","href":"/civicsaurus/docs/project-lifecycle/reframe","docId":"project-lifecycle/reframe","unlisted":false},{"type":"link","label":"3.3 Develop","href":"/civicsaurus/docs/project-lifecycle/create","docId":"project-lifecycle/create","unlisted":false},{"type":"link","label":"3.4 Deliver","href":"/civicsaurus/docs/project-lifecycle/catalyze","docId":"project-lifecycle/catalyze","unlisted":false},{"type":"link","label":"3.5 Evolve","href":"/civicsaurus/docs/project-lifecycle/evolve","docId":"project-lifecycle/evolve","unlisted":false}],"href":"/civicsaurus/docs/category/3-project-lifecycle"}]},"docs":{"intro":{"id":"intro","title":"Welcome","description":"Welcome to the Civic Tech Playbook. This resource is designed to help you create, develop, and implement impactful civic technology projects. Whether you\'re a seasoned practitioner or new to the field, this playbook offers best practices, and practical advice to help you navigate the exciting world of civic tech.","sidebar":"tutorialSidebar"},"principles/iterative-and-responsive":{"id":"principles/iterative-and-responsive","title":"1.3 Iterative and Responsive","description":"Always be learning and adapting. Listen to and respond to community feedback by regularly testing and refining solutions.","sidebar":"tutorialSidebar"},"principles/open-and-transparent":{"id":"principles/open-and-transparent","title":"1.2 Open and Transparent","description":"Embrace openness in processes, data, code and content. Foster trust and collaboration by sharing knowledge and inviting participation. Strive to be \u2018open source\u2019 not only with your code, but also with your ideas.","sidebar":"tutorialSidebar"},"principles/sustainable-and-scalable":{"id":"principles/sustainable-and-scalable","title":"1.4 Sustainable and Scalable","description":"Where possible, design solutions with longevity and growth in mind. Create projects that can be maintained, expanded, and replicated to increase your long-term (and long-tail) impact.","sidebar":"tutorialSidebar"},"principles/user-centered-and-inclusive":{"id":"principles/user-centered-and-inclusive","title":"1.1 User Centered and Inclusive","description":"Put the needs of community members at the heart of every project. Ensure that solutions are accessible, equitable, and grounded in user needs. In civic tech, we try to *build with not for* our communities.","sidebar":"tutorialSidebar"},"project-lifecycle/catalyze":{"id":"project-lifecycle/catalyze","title":"3.4 Deliver","description":"Put your solution out there for people to use. Keep an eye on how it\'s working and make it better over time.","sidebar":"tutorialSidebar"},"project-lifecycle/create":{"id":"project-lifecycle/create","title":"3.3 Develop","description":"Come up with ideas and start building. Try things out, get feedback, and keep improving.","sidebar":"tutorialSidebar"},"project-lifecycle/evolve":{"id":"project-lifecycle/evolve","title":"3.5 Evolve","description":"Decide on the next steps for your project. This could mean growing it, handing it off, or winding it down based on how it\'s working and what the community needs.","sidebar":"tutorialSidebar"},"project-lifecycle/explore":{"id":"project-lifecycle/explore","title":"3.1 Discover","description":"Get to know your community and the problem you\'re trying to solve. Talk to people, observe, and gather information.","sidebar":"tutorialSidebar"},"project-lifecycle/reframe":{"id":"project-lifecycle/reframe","title":"3.2 Define","description":"Figure out exactly what needs fixing. This is where you narrow down your focus based on what you learned.","sidebar":"tutorialSidebar"},"ways-of-working/connection-and-relationship":{"id":"ways-of-working/connection-and-relationship","title":"2.2 Connection and Relationship","description":"Create and maintain collaborative relationships within your team and with the broader community.","sidebar":"tutorialSidebar"},"ways-of-working/continuing-the-journey":{"id":"ways-of-working/continuing-the-journey","title":"2.4 Continuing the Journey","description":"Be intentional about the future of the project, whether that\u2019s scaling it, handing it off to another team or organization, or shuttering it.","sidebar":"tutorialSidebar"},"ways-of-working/leadership-and-storytelling":{"id":"ways-of-working/leadership-and-storytelling","title":"2.3 Leadership and Storytelling","description":"Keep the team motivated and craft narratives that inspire action and communicate the value of your work.","sidebar":"tutorialSidebar"},"ways-of-working/orientation-and-vision-setting":{"id":"ways-of-working/orientation-and-vision-setting","title":"2.1 Vision Setting","description":"Establish a clear direction for your project, and align team members and stakeholders around common goals and values.","sidebar":"tutorialSidebar"}}}}')}}]);